    pub fn shifting_letters(s: String, shifts: Vec<Vec<i32>>) -> String {
        let letters = ('a'..='z').collect::<Vec<_>>();
        let s = s.chars().map(|x| x as i32 - 'a' as i32).collect::<Vec<_>>();

        let mut diff = vec![s[0]];
        for i in 1..s.len() {
            diff.push(s[i] - s[i - 1]);
        }
        let n = diff.len();

        for sh in shifts {
            let (left, right, val) = (
                sh[0] as usize,
                sh[1] as usize,
                if sh[2] == 0 { -1 } else { 1 },
            );
            diff[left] += val;
            diff[left] %= 26;
            if right + 1 < n {
                diff[right + 1] -= val;
                diff[right] %= 26;
            }
        }
        // converting to prefix sum, e.g. new original array
        for i in 1..n {
            diff[i] += diff[i - 1] % 26;
            diff[i] %= 26;
        }

        diff.into_iter()
            .map(|x| letters[((x + 26) % 26) as usize])
            .collect()
    }
}
