func shiftingLetters(s string, shifts [][]int) string {
    var n int = len(s)
    prefix := make([]int, n)
    for _, shift := range shifts {
        val := -1
        if shift[2] == 1 { val = 1 }
        prefix[shift[0]] += val
        if shift[1] + 1 < n {
            prefix[shift[1] + 1] -= val
        }
