class Solution {
    public String shiftingLetters(String s, int[][] shifts) {
        int N = s.length();
        int[] A = new int[N+1];
        for(int[] S : shifts){
            if(S[2] == 1){
                A[S[0]]++;
                A[S[1]+1]--;
            }else{
                A[S[0]]--;
                A[S[1]+1]++;
            }
        }

        int shift = 0;
        StringBuilder sb = new StringBuilder();
        for(int i=0; i<N; i++){
            shift += A[i];
            int n = (((s.charAt(i) - 'a') + shift) % 26 + 26) % 26;
            sb.append((char)('a' + n));
        }
        return sb.toString();
    }

}
