    def shiftingLetters(self, s: str, shifts: List[List[int]]) -> str:
        forward_starts = [0]*len(s)
        forward_ends = [0]*len(s)
        back_starts = [0]*len(s)
        back_ends = [0]*len(s)

        for shift in shifts:
            start, end, direction = shift
            if direction == 1:
                forward_starts[start] += 1
                forward_ends[end] += 1
            else:
                back_starts[start] += 1
                back_ends[end] += 1

        res = []
        shift_amount = 0
        for i, ch in enumerate(s):
            shift_amount += forward_starts[i]
            shift_amount -= back_starts[i]

            res.append(self.shift_letter(ch, shift_amount))

            shift_amount -= forward_ends[i]
            shift_amount += back_ends[i]

        return "".join(res)

    def shift_letter(self, letter, amount):
        letter_index = ord(letter) - ord("a")
        new_letter_index = (letter_index + amount) % 26
        return chr(new_letter_index + ord("a"))

```

