class Solution {
public:
    #define ll long long

    static bool cmp(vector<ll>&v1,vector<ll>&v2){
        if(v1[0]==v2[0]){
            return v1[1]>v2[1];
        }
        return v1[0]<v2[0];
    }

    string shiftingLetters(string s, vector<vector<int>>& shifts) {
        ll current=0,index=0,mayank=0;
        vector<vector<ll>>container;
        string answer=s;
        for(auto &x:shifts){
            container.push_back({x[0],1,(x[2]==1)?1:-1});
            container.push_back({x[1],-1,(x[2]==1)?1:-1});
        }
        ll n=s.length();
        sort(container.begin(),container.end(),cmp);
        while(mayank<n){
            ll now=mayank;
            while(index<container.size() && container[index][0]==now && container[index][1]==1){
                current+=container[index][2];
                index++;
            }
            bool condition=(current<0?true:false);
            current=abs(current)%26;
            if(condition){
                current=-current;
            }
            for(ll i=0;i<abs(current);i++){
                if(current>0){
                    answer[mayank]++;
                    if(answer[mayank]>'z'){
                        answer[mayank]='a';
                    }
                }
                else{
                    answer[mayank]--;
                    if(answer[mayank]<'a'){
                        answer[mayank]='z';
                    }
                }
            }
            while(index<container.size() && container[index][0]==now && container[index][1]==-1){
                current-=container[index][2];
                index++;
            }
            mayank++;
        }
        return answer;
    }
};
