/**
 * @param {string} s
 * @param {number[][]} shifts
 * @return {string}
 */
var shiftingLetters = function (s, shifts) {
	const arr = new Array(s.length).fill(0);
	for (const [start, end, direction] of shifts) {
		if (direction === 0) {
			arr[start] -= 1;
			end !== arr.length - 1 && (arr[end + 1] += 1)
		} else {
			arr[start] += 1;
			end !== arr.length - 1 && (arr[end + 1] -= 1)
		}
	}

	for (let i = 1; i < arr.length; i++) arr[i] += arr[i - 1]

	let res = ''
	for (let i = 0; i < arr.length; i++) {
		const code = s[i].charCodeAt() + arr[i];

		if (code < 97) {
			let diff = (97 - code) % 26;
            // to handle negative and zero
			if (diff <= 0) diff += 26
			res += String.fromCharCode(123 - diff);
		}
		else if (code > 122) {
			let diff = (code - 122) % 26;
			if (diff <= 0) diff += 26
			res += String.fromCharCode(96 + diff);
		}
		else res += String.fromCharCode(code)
	}

	return res;
};
