func shiftingLetters(s string, shifts [][]int) string {
	shiftRecorder := make([]int, len(s)+1)
	for _, shift := range shifts {
		if shift[2] == 1 {
			shiftRecorder[shift[0]]++
			shiftRecorder[shift[1]+1]--
		} else {
			shiftRecorder[shift[0]]--
			shiftRecorder[shift[1]+1]++
		}
	}
	bb := bytes.Buffer{}
	move := 0
	for i, c := range s {
		move += shiftRecorder[i]
		if move < 0 {
			move = (26 - (-move)%26) % 26
		}
		move %= 26
		nc := c + rune(move)
		if nc > 'z' {
			nc -= 26
		}
		bb.WriteRune(nc)
	}
	return bb.String()
}
