class Solution {
public:
	string shiftingLetters(string s, vector<vector<int>>& shifts) {
		int n = s.size();
		vector<int> preSum(n+1,0);
		for(auto it : shifts){
			int st = it[0] , e = it[1];
			int d = it[2];
			if(d) {
				preSum[st] += 1;
				preSum[e+1] += (-1);
			}else{
				preSum[st] += (-1);
				preSum[e+1] += 1;
			}
		}

		int sum = 0;
		for(int i = 0 ; i < n ; i++){
			sum += preSum[i];
			int val = (s[i] - 'a' + sum%26 + 26) % 26;
			s[i] = 'a' + val;
		}
		return s;
	}
};

