var shiftingLetters = function(s, shifts) {
	let n = s.length;
    let pfx = new Array(n).fill(0);

    for(let i = 0; i < shifts.length; i++) {
        let [start, end, dir] = shifts[i];

        for(let j = start; j <= end; j++) {

            if(dir === 1) {
                pfx[j] += 1;
            } else {
                pfx[j] -= 1;
            }

        }
    }

    let str = "";

    for(let i = 0; i < s.length; i++) {
        let t = s[i].charCodeAt(0) - 97;
        let tsign = t + pfx[i] < 0 ? 26 + (t + pfx[i]) % 26: t + pfx[i];
        let code = 97 + tsign % 26;
        let ch = String.fromCharCode(code);
        str += ch;
    }

    return str;
}
