class Solution:
    def shiftingLetters(self, st: str, shifts: List[List[int]]) -> str:

        deltas = [0] * (len(st) + 1)
        # deltas = dict()
        for s, e, d in shifts:
            d = (d << 1) - 1
            deltas[s] += d
            deltas[e + 1] -= d


        shifted = []
        cur_shift = 0
        for delta, letter in zip(deltas, st):
            cur_shift += delta
            if cur_shift != 0:
                letter = chr(97 + (ord(letter) - 97 + cur_shift) % 26)
            shifted.append(letter)

        return "".join(shifted)


