int64_t e_group_op = 0;

struct treeNode {
    treeNode() : s(0), g(e_group_op), num(1) {}
	treeNode (int64_t val, int64_t g_val=e_group_op, int n=1) : s(val), g(g_val), num(n) {}
    int64_t s, g;
    int num;
};

treeNode e_op = {0, e_group_op, 0};


void group_op(treeNode& x, int64_t val) {
    // x.g += val;
    // x.s += val * x.num;
    // ----------------------------------
    // if (x.g < val) x.s = x.g = val;
    // ----------------------------------
    x.g += val;
    x.s = x.g;
}

treeNode main_tree_op(const treeNode& x, const treeNode& y) {
    return treeNode(x.s + y.s, e_group_op, x.num + y.num);
    // ------------------------------------------------------
    // return treeNode(max(x.s, y.s));
}


class segmentTree {
private:
    bool rightInclude;

    int upper_power2(int x) {
        int power2 = 1;
        while (power2 < x) {
            power2 *= 2;
        }
        return power2;
    }

    int n, N;

public:
    vector<treeNode> tree;
    segmentTree() {}
    segmentTree(vector<int>& arr, bool rightInclusive=false, bool initialize=true) {
        rightInclude = rightInclusive;
        n = arr.size();
        N = 2 * upper_power2(n) - 1;
        tree.assign(N, e_op);
        if (initialize) {
            for (int i = 0; i < n; ++i) {
                tree[N / 2 + i] = arr[i];
            }
            for (int x = N - 1; x > 1; x -= 2) {
                tree[(x - 1) / 2] = main_tree_op(tree[x], tree[x - 1]);
            }            
        }
    }

    void propagate(int x, int lx, int rx) {
        if (rx - lx == 1 || tree[x].g == e_group_op) return;
        group_op(tree[2 * x + 1], tree[x].g);
        group_op(tree[2 * x + 2], tree[x].g);
        tree[x].g = e_group_op;
    }

    void set(int i, int64_t v, int x=0, int lx=0, int rx=-1) {
        if (rx == -1) rx = N / 2 + 1;            
        propagate(x, lx, rx);
        if (rx - lx == 1) {
            tree[x].s = v;
            return;
        }
        int m = (rx + lx) / 2;
        if (i < m) {
            set(i, v, 2 * x + 1, lx, m);
        } else {
            set(i, v, 2 * x + 2, m, rx);
        }
        tree[x] = main_tree_op(tree[2 * x + 1], tree[2 * x + 2]);
    }

    int64_t query(int l, int r, int x=0, int lx=0, int rx=-1) {
        if (rx == -1) {
            rx = N / 2 + 1;
            if (rightInclude) r += 1;
        }

        propagate(x, lx, rx);

        if (lx >= r || l >= rx) return e_op.s;
        if (lx >= l && rx <= r) return tree[x].s;
        int m = (rx + lx) / 2;
        int64_t ans_l = query(l, r, 2 * x + 1, lx, m);
        int64_t ans_r = query(l, r, 2 * x + 2, m, rx);
        return main_tree_op(ans_l, ans_r).s;
    }

    void group_change(int l, int r, int64_t group_val, int x=0, int lx=0, int rx=-1) {
        if (rx == -1) {
            rx = N / 2 + 1;
            if (rightInclude) r += 1;
        }

        propagate(x, lx, rx);

        if (lx >= r || l >= rx) return;
        if (lx >= l && rx <= r) {
            group_op(tree[x], group_val);
            return;
        }
        int m = (rx + lx) / 2;
        group_change(l, r, group_val, 2 * x + 1, lx, m);
        group_change(l, r, group_val, 2 * x + 2, m, rx);
        tree[x] = main_tree_op(tree[2 * x + 1], tree[2 * x + 2]);
    }

    void print() {
        for (int i = 0; i < tree.size(); ++i) {
            cout << "\"" << tree[i].s << "|" << tree[i].g << "\"" << ", ";
        }
        cout << endl;
    }
};


class Solution {
public:
    segmentTree st;

    string shiftingLetters(string s, vector<vector<int>>& shifts) {
        vector<int> tmp(s.size());
        st = segmentTree(tmp, true, false);
        int dir;
        for (int i = 0; i < shifts.size(); ++i) {
            dir = (shifts[i][2] == 0) ? -1 : 1;
            st.group_change(shifts[i][0], shifts[i][1], dir);
        }
        for (int i = 0; i < s.size(); ++i) {
            s[i] = (((s[i] - 'a' + st.query(i, i)) % 26) + 26) % 26 + 'a';
        }
        return s;
    }
};
