class Solution {
public:
    string shiftingLetters(string s, vector<vector<int>>& shifts) {
        vector<pair<int,int>>v(s.size()+1);
        //{no_of_zeroes,no_of_ones}
        for(int i=0;i<shifts.size();i++)
        {
            if(shifts[i][2]==0)
            {
                v[shifts[i][0]].first++;
                v[shifts[i][1]+1].first--;
            }
            else
            {
                v[shifts[i][0]].second++;
                v[shifts[i][1]+1].second--;
            }
        }
