class Solution {
public:
    string shiftingLetters(string s, vector<vector<int>>& shifts) {
        int n=s.size();
        vector<int> v(n, 0);//for storing from which index to which index forward or backward operation takes place
        for(int i=0;i<shifts.size();i++){
            if(shifts[i][2]){
                //forward operation
                if(shifts[i][1]+1<n){
                    v[shifts[i][1]+1]-=1;
                }
                v[shifts[i][0]] += 1;
            }
            else{
                //backward operation
                if(shifts[i][1]+1<n){
                    v[shifts[i][1]+1]+=1;
                }
                v[shifts[i][0]] -= 1;
            }
        }
        //summing up everything to we will know what operation to do and howmany times we should do
        int num=v[0];
        for(int i=1;i<n;i++){
            v[i] += v[i-1];
        }
        //converting the character
        for (int j = 0; j < s.size(); j++) {
			int num = s[j] - 'a';
			num += v[j];
            num %= 26;
            num += 26;
			num %= 26;
			s[j] = 'a' + num;
		}
        return s;
    }
};
