#define ll long long
struct DSU {
    vector<ll> par, lev, sum;

    //sum -> to store the sum of a segment
    //currently every index represent a segment

    DSU(int n, vector<int> &ar) {
        par.resize(n, 0);
        lev.resize(n, 1);
        sum.resize(n);
        for(int i = 0; i < n; ++i) sum[i] = ar[i];
        iota(par.begin(), par.end(), 0);
    }

    int fset(int v) {
        return v == par[v] ? v : par[v] = fset(par[v]);
    }

    void uset(int u, int v) {
        u = fset(u), v = fset(v);
        if(u != v) {
            if(lev[u] < lev[v]) swap(u, v);
            par[v] = u;
            sum[u] += sum[v]; // if two segments are merged the sum increases
        }
    }

    ll getSum(int v) {
        return sum[fset(v)]; // the total sum of the segment would be stored in the parent node
    }
};
class Solution {
public:
    vector<long long> maximumSegmentSum(vector<int>& nums, vector<int>& v) {
        int n = nums.size();
        DSU dsu(n, nums);

        vector<ll> res(n, 0), cur(n, 0);
        ll mx = 0;

        //work your way towards the back
        for(int i = v.size() - 1; ~i; --i) {
            res[i] = mx; // place the current maximum

            cur[v[i]] = nums[v[i]]; //place the value

            if(v[i] + 1 < n and cur[v[i] + 1] != 0) dsu.uset(v[i], v[i] + 1); 
            // if the value at right index is not zero so the current index can be merged with the right segment

            if(v[i] - 1 >= 0 and cur[v[i] - 1] != 0) dsu.uset(v[i], v[i] - 1);
            // if the value at left index is not zero so the current index can be merged with the left segment

            mx = max(mx, dsu.getSum(v[i])); //after merging get the sum of the current segment formed placing the v[i]th element
        }

        return res;
    }
};
```

