#define ll long long int
class Solution {
public:
    vector<long long> maximumSegmentSum(vector<int>& nums, vector<int>& removeQueries) {
        int n = nums.size();
        vector<ll> pre(n+1,0);
        multiset<ll> sums;
        map<int,pair<int,int>> next;
        for(int i=0;i<n;i++){
            pre[i+1] = pre[i]+nums[i];
        }
        sums.insert(pre[n]);
        next[n-1] = {0,n-1};
        vector<ll> ans;
        for(int i=0;i<removeQueries.size();i++){
            int idx = removeQueries[i];
            auto tmp = *next.lower_bound(idx);
            pair<int,int> pp = next[tmp.first];
            if(pp.first==idx){
                next.erase(pp.second);
                next[pp.second] = {idx+1,pp.second};
                auto it = sums.find(pre[pp.second+1]-pre[pp.first]);
                sums.erase(it);
                sums.insert(pre[pp.second+1]-pre[idx+1]);
            }
            else if(pp.second==idx){
                next.erase(pp.second);
                next[idx-1] = {pp.first,idx-1};;
                auto it = sums.find(pre[pp.second+1]-pre[pp.first]);
                sums.erase(it);
                sums.insert(pre[idx]-pre[pp.first]);
            }
            else{
                next.erase(pp.second);
                next[idx-1] = {pp.first,idx-1};
                next[pp.second] = {idx+1,pp.second};
                auto it = sums.find(pre[pp.second+1]-pre[pp.first]);
                sums.erase(it);
                sums.insert(pre[pp.second+1]-pre[idx+1]);
                sums.insert(pre[idx]-pre[pp.first]);
            }
            ans.push_back(*sums.rbegin());
        }
        return ans;

    }
};
