Approach 1: Union Find (24ms)
```
class Solution {
    public long[] maximumSegmentSum(int[] nums, int[] removeQueries) {
        int n = nums.length;
        long[] ans = new long[n];
        UnionFind uf = new UnionFind(n, nums);

        for (int i=n-1; i>0; i--) {
            int cur = removeQueries[i];
            uf.add(cur, nums[cur]);

            if (cur > 0 && uf.getSum(cur-1) > 0)
                uf.union(cur, cur - 1);
            if (cur < n-1 && uf.getSum(cur+1) > 0)
                uf.union(cur, cur + 1);

            int root = uf.find(cur);
            ans[i-1] = Math.max(uf.getSum(root), ans[i]);
        }

        return ans;
    }
}

class UnionFind {
    private int[] root;
    private int[] rank;
    private long[] sum;

    public UnionFind(int size, int[] arr) {
        root = new int[size];
        rank = new int[size];
        sum = new long[size];

        for (int i = 0; i < size; i++) {
            root[i] = i;
            rank[i] = 1; 
        }
    }

    public int find(int x) {
        if (x == root[x]) {
            return x;
        }
        return root[x] = find(root[x]);
    }

    public void union(int x, int y) {
        int rootX = find(x);
        int rootY = find(y);

        if (rootX != rootY) {
            if (rank[rootX] > rank[rootY]) {
                root[rootY] = rootX;
                sum[rootX] += sum[rootY];
            } else if (rank[rootX] < rank[rootY]) {
                root[rootX] = rootY;
                sum[rootY] += sum[rootX];
            } else {
                root[rootY] = rootX;
                rank[rootX] += 1;
                sum[rootX] += sum[rootY];
            }
        }
    }

    public void add(int idx, int val) {
        sum[idx] += val;
    }

    public long getSum(int idx) {
        return sum[idx];
    }
}
