    }

    public void union(int x, int y) {
        int rootX = find(x);
        int rootY = find(y);

        if (rootX != rootY) {
            if (rank[rootX] > rank[rootY]) {
                root[rootY] = rootX;
                sum[rootX] += sum[rootY];
            } else if (rank[rootX] < rank[rootY]) {
                root[rootX] = rootY;
                sum[rootY] += sum[rootX];
            } else {
                root[rootY] = rootX;
                rank[rootX] += 1;
                sum[rootX] += sum[rootY];
            }
        }
    }

    public void add(int idx, int val) {
        sum[idx] += val;
    }

    public long getSum(int idx) {
        return sum[idx];
    }
}
