class Solution {
	// once we have defined our segment tree node logic (see below), the implementation of the core algorithm is trivial:
    public long[] maximumSegmentSum(int[] nums, int[] removeQueries) {
        int n= nums.length, k= removeQueries.length;
        SegmentTree st= new SegmentTree(nums);
        long[] ans= new long[k];
        for(int i=0; i<k; i++){
            st.setZero(removeQueries[i]);
            ans[i]= st.rangeQuery(0, n-1).maxSum;
        }
        return ans;
    }

    static class SegmentTree {
        int n;      // size of the original array
        int[] A;    // original array
        Node[] st;  // segment tree array

        public SegmentTree(int[] nums){
            this.A= nums;
            this.n= nums.length;
            this.st= new Node[4*n];
            build(1, 0, n-1);   // build recursively top-down (p==1 index of the tree root (top) node)
        }

        public Node rangeQuery(int i, int j){
            return rangeQuery(1, 0, n-1, i, j);      // traverse segment tree from the root
        }

        public void setZero(int i){   // set single element's value to zero
            update(1, 0, n-1, i, i, 0);
        }

	  // node contains the minimum information required for segment tree to calculate a max subarray sum (that doesn't contain zeros) 
	  // and allow for bottom-up merging of that information
        static class Node{         
            boolean hasZero;       // true if contains some deleted elements
            long maxSum;           // max sum of subarray with no zeros
            long lSum;             // sum from left to first zero
            long rSum;             // sum from right to first zero

            Node(long val){
                this.hasZero= val==0;
                this.maxSum= this.lSum= this.rSum= val;
            }

            Node(boolean hasZero, long maxSum, long lSum, long rSum){
                this.hasZero= hasZero;
                this.maxSum= maxSum;
                this.lSum= lSum;
                this.rSum= rSum;
            }

			// node merging logic to calculate the max subarray sum (not containing zeros)
			static Node merge(Node l, Node r){
                if(l==null) return r;
                if(r==null) return l;
                if(!l.hasZero && !r.hasZero) return new Node(l.maxSum+r.maxSum);
                return new Node(true, 
                    Math.max(l.rSum+r.lSum, Math.max(l.maxSum, r.maxSum)),
                    l.hasZero ? l.lSum : l.maxSum+r.lSum,
                    r.hasZero ? r.rSum : r.maxSum+l.rSum
                );
            }  	
        }

		// boilerplate, reusable implementation of segment tree operations follows
        void build(int p, int L, int R){
            if(L==R){
                st[p]= new Node(A[L]);
            }else{
                int m= (L+R)/2, lp= (p<<1), rp= lp+1;
                build(lp, L, m);
                build(rp, m+1, R);
                st[p]= Node.merge(st[lp], st[rp]); 
            }
        }        

        void update(int p, int L, int R, int i, int j, int val){
            if(i>j) return;
            if((L>=i) && (R<=j)){
                st[p]= new Node(A[i]= val);
            }else{
                int m= (L+R)/2, lp= p*2, rp= lp+1;
                update(lp, L, m, i, Math.min(m,j), val);
                update(rp, m+1, R, Math.max(i,m+1), j, val);
                st[p]= Node.merge(st[lp], st[rp]);
            }
        }

        Node rangeQuery(int p, int L, int R, int i, int j){
            if(i>j) return null;
            if((L>=i) && (R<=j)) return st[p];        // found the segment
            int m= (L+R)/2, lp= p*2, rp= lp+1;
            return Node.merge(rangeQuery(lp, L, m, i, Math.min(m,j)),
                              rangeQuery(rp, m+1, R, Math.max(i, m+1), j));
        }
    }
}

