class Solution {
    long long getSum(vector<long long>& BITree, int index) {
        long long sum = 0;
        index++;
        while (index>0) {
            sum += BITree[index];
            index -= index & (-index);
        }
        return sum;
    }

    long long getSum(vector<long long>& BITree, int l, int r) {
        return getSum(BITree, r) - getSum(BITree, l - 1);
    }

    void updateBIT(vector<long long>& BITree, int n, int index, long long val) {
        index++;
        while (index <= n) {
            BITree[index] += val;
            index += index & (-index);
        }
    }
public:
    vector<long long> maximumSegmentSum(vector<int>& nums, vector<int>& removeQueries) {
        multiset<long long> pq;
        set<long long> query;
        vector<long long> result;

        // Constructs BIT
        int n=nums.size();
        vector<long long> BITree(n+1, 0);
        for (int i=0; i<n; i++)
            updateBIT(BITree, n, i, nums[i]);

        for(int i=0;i<removeQueries.size();i++) {
            long long l=0;
            if (!query.empty()) {
                auto it = query.lower_bound(removeQueries[i]);
                if (it!=query.begin()) {
                    it--;
                    l = (*it);
                }
            }

            long long r=n-1;
            if (!query.empty()) {
                auto it = query.upper_bound(removeQueries[i]);
                if (it!=query.end()) r = (*it);
            }

            long long leftSum = (removeQueries[i]>0) ? getSum(BITree, l, removeQueries[i]-1) : 0;
            long long rightSum = (removeQueries[i]!=n-1) ? getSum(BITree, removeQueries[i]+1, r) : 0;

            if (pq.size()!=0) pq.erase(pq.find(leftSum+rightSum+nums[removeQueries[i]]));

            updateBIT(BITree, n, removeQueries[i], -nums[removeQueries[i]]);
            nums[removeQueries[i]]=0;

            pq.insert(leftSum);
            pq.insert(rightSum);
            result.push_back(*pq.rbegin());
            query.insert(removeQueries[i]);
        }
        return result;
    }
};
