class Solution {
public:

    using ll = long long;

    ll findSum(int start, int end, vector<ll> &prefixSum) {
        if (start == 0) return prefixSum[end];
        return prefixSum[end] - prefixSum[start-1];
    }

    vector<long long> maximumSegmentSum(vector<int>& nums, vector<int>& removeQueries) {
        int n = nums.size();
        vector<long long> prefixSum(n,nums[0]);
        prefixSum[0] = nums[0];

        // compute prefix Sum
        for (int i = 1; i < n; i ++)
            prefixSum[i] = prefixSum[i-1] + nums[i];

        set<pair<int,int>> segSet;
        multiset<ll> sumSet; // multi set as duplicate set sum possible


        // first Segment is full one as there are all non zero numbers
        segSet.insert({0,n-1}); // inserting full segment
        sumSet.insert(prefixSum[n-1]); 

        vector<ll> result;
        // last entry will always resturn 0 so only traverse till n-1
        for (int i = 0; i < n-1; i++) {
            int rIndex = removeQueries[i];

            // check with set this rIndex belongs to
            auto it = segSet.upper_bound({rIndex,rIndex});

            //if this is now at end of the list ie no upper bound found
            if (it == segSet.end()) 
                it--;
            else // else check if the landed is not part of the upper boundsegment 
                if (it->first > rIndex) it--;

            // Now we found the exact set
            // now compute the acutal reduction
            auto p = *it;
            ll value = findSum(p.first,p.second,prefixSum);
            // remove this set from the segement list as it will be splitted now
            segSet.erase(it);

            //remove the sum of erased segment from set
            auto valueIt = sumSet.lower_bound(value);
            sumSet.erase(valueIt);

            ll value1 = -1;
            ll value2 = -1;

            // if removed index is starting the current set
            if (p.first == rIndex) {
                segSet.insert({rIndex+1,p.second});
                value1 = findSum(rIndex+1,p.second,prefixSum);

            } else if (p.second == rIndex) { 
                // if removed index is ending the current set
                segSet.insert({p.first,rIndex-1});
                value2 = findSum(p.first,rIndex-1,prefixSum);

            } else {
                // if removed element is middle and splitting the set
                segSet.insert({p.first,rIndex-1});
                value1 = findSum(p.first,rIndex-1,prefixSum);
                segSet.insert({rIndex+1,p.second});
                value2 = findSum(rIndex+1,p.second,prefixSum);

            }

            // Insert new found segment set sum
            if (value1 != -1) sumSet.insert(value1);
            if (value2 != -1) sumSet.insert(value2);

            // append the last element of the sorted set which is current maxiumm
            // after new sets has been inserted.
            result.push_back(*sumSet.rbegin());
        }

        // End result is always 0
        result.push_back(0);
        return result;
    }

