This is simply how we find maximal sum segment in an array using Segment tree with slight modification. Since we want the original segment into be divided into parts, We update the division boundary by a large negative value so that this element will never be included in maximal sum segment.

```
#define ll long long
struct item{
      ll sum,pre,suf,seg;
};

struct sgmt{
  ll n;
  vector<item> st;
    item NEUTRAL_ELEMENT = {0,0,0,0};
    item single(ll v)
    {
            item ret;
            ret.sum = v;
            ret.pre = max(0ll,v);
            ret.suf = max(0ll,v);
            ret.seg = max(0ll,v);
            return ret;
    }
    sgmt(int _n){                                                 //update
            n = _n;           
            st.resize(4*n,NEUTRAL_ELEMENT);                    
    }

    item merge(item a,item b)                                     //update 
    {
           item res;
           res.pre = max(a.pre,a.sum + b.pre);
           res.suf = max(b.suf,b.sum + a.suf);
           res.seg = max(max(a.seg,b.seg),a.suf + b.pre);
           res.sum = a.sum + b.sum;
           return res;
    }

    void build(int si, int ei, int node, vector<ll>&v){
        if(si==ei){
            st[node] = single(v[si]);                              //check
            return;
        }
        int mid = (si+ei)/2;
        build(si,mid,node*2+1,v);
        build(mid+1,ei,node*2+2,v);
        st[node] = merge(st[node*2+1],st[node*2+2]);
    }

    item query(int si,int ei,int node, int l, int r){
        if(r<si or l>ei) return NEUTRAL_ELEMENT;
        if(l<=si and r>=ei) return st[node];
        int mid = (si+ei)/2;
        item q1 = query(si,mid,node*2+1,l,r);
        item q2 = query(mid+1,ei,node*2+2,l,r);
        return merge(q1,q2);
    }

    void update(int si, int ei,int node,int idx, ll val){
        if(si==ei){
            st[node]= single(val);                                    //update the value
            return;
        }
        int mid = (si+ei)/2;
        if(idx<=mid) {
            update(si,mid,node*2+1,idx,val);
        }
        else{
            update(mid+1,ei,node*2+2,idx,val);
        }
        st[node] = merge(st[node*2+1],st[node*2+2]);
    }
    item query(ll l, ll r){
      return query(0,n-1,0,l,r);                                 //print whatever required

    }
    void build(vector<ll>&v){

        build(0,n-1,0,v);
    }
    void update(ll idx,ll val){
        update(0,n-1,0,idx,val);
    }

};
class Solution {
public:
    vector<long long> maximumSegmentSum(vector<int>& nu, vector<int>& re) {
        ios_base::sync_with_stdio(0);
        cin.tie(0);
        cout.tie(0);
     sgmt s(nu.size());
        vector<ll> nums(nu.begin(),nu.end());
        s.build(nums);
        vector<ll> ans;
        for(int i = 0;i<re.size();i++){
            s.update(re[i],-1e14+1e13);
            ans.push_back(s.query(0,nu.size()).seg);
        }
            return ans;
    }
};
```

