from sortedcontainers import SortedDict

class Solution:
    def maximumSegmentSum(self, nums: List[int], removeQueries: List[int]) -> List[int]:
        n = len(nums)
        presum =[0] * (n + 1)
        for i in range(n): presum[i + 1] = presum[i] + nums[i]
        rng2v, v2rng = SortedDict({(0, n - 1): presum[n]}), SortedDict({presum[n] : {(0, n - 1)}})
        ans = []
        for idx in removeQueries:
            pos = min(rng2v.bisect_left((idx, idx)), len(rng2v) - 1)
            if not (rng2v.iloc[pos][0] <= idx <= rng2v.iloc[pos][1]): pos -= 1
            (left, right), val = rng2v.popitem(pos)
            v2rng[val].remove((left, right))
            if not v2rng[val]: v2rng.pop(val)
            if left < idx:
                lv = presum[idx] - presum[left]
                rng2v[(left, idx - 1)] = lv
                if lv not in v2rng: v2rng[lv] = set()
                v2rng[lv].add((left, idx - 1))
            if idx < right:
                rv = presum[right + 1] - presum[idx + 1]
                rng2v[(idx + 1, right)] = rv
                if rv not in v2rng: v2rng[rv] = set()
                v2rng[rv].add((idx + 1, right))
            ans.append(v2rng.iloc[-1] if v2rng else 0)
        return ans
