#define ll long long
class Solution {
public:
    int find(ll i,vector<ll> &par){
        if(par[i]==i) return i;
        return par[i]=find(par[i],par);
    }
    void merge(ll a,ll b,vector<ll> &par,vector<ll> &rank){
        a=find(a,par);
        b=find(b,par);
        if(a==b) return;
        if(rank[a]>=rank[b]){
            rank[a]+=rank[b];
            par[b]=a;
        }else{
            rank[b]+=rank[a];
            par[a]=b;
        }return;
    }
    vector<long long> maximumSegmentSum(vector<int>& v, vector<int>& q) {
        map<ll,ll> m;
        int n=v.size();
        vector<ll> ans;
        reverse(q.begin(),q.end());
        ans.push_back(0);
        if(n==1) return ans;
        ans.push_back(v[q[0]]);
        m[q[0]]++;
        vector<ll> par(n),rank(n,0);
        for(int i=0;i<n;i++){
            par[i]=i;
            rank[i]=v[i];
        }
        ll maxi=v[q[0]];
        for(int i=1;i<n-1;i++){
            ll a=q[i];
            if(m.find(a-1)!=m.end()){
                merge(a-1,a,par,rank);
                ll p=find(a,par);
                maxi=max({maxi,rank[p]});
            }
            if(m.find(a+1)!=m.end()){
                merge(a+1,a,par,rank);
                ll p=find(a,par);
                maxi=max({maxi,rank[p]});
            }
            m[a]++;
            maxi=max(maxi,(ll)v[q[i]]);
            ans.push_back(maxi);
        }
        reverse(ans.begin(),ans.end());
        return ans;

    }
};``
