class Solution {
public:
    #define ll long long
    ll par[100005];
    ll sz[100005];
    ll find(int x){
        if(x<0) return x;
        if(par[x]==x) return x;
        return par[x]=find(par[x]);
    }
    vector<long long> maximumSegmentSum(vector<int>& nums, vector<int>& q) {
        priority_queue<ll>pq;
        vector<ll>ans;
        for(int i=0;i<=nums.size();i++) par[i]=i,sz[i]=0;
        pq.push(0);
        for(int i=q.size()-1;i>=0;i--){
            int idx=q[i];
            ll curmax=pq.top();
           ///cout<<curmax<<" ";
            ans.push_back(curmax);
            ll left=find(idx-1);
            ll right=find(idx+1);
            ll cur=find(idx);
            sz[cur]=nums[cur];
            ///cout<<idx<<endl;
            //cout<<sz[cur]<<" "<<endl;
            if(left!=-1 and sz[left]>0){
                sz[cur]+=sz[left];
                par[left]=cur;
            }
            if(sz[right]>0){
                sz[cur]+=sz[right];
                par[right]=cur;
            }
           // cout<<cur<<" ";

            pq.push(sz[cur]);
        }
        reverse(ans.begin(),ans.end());
    return ans;
    }
};
