```class Solution {
public:

    #define ll long long
    struct Node{
	ll s;
	ll pre;
	ll suf;
	ll re;
};

vector<Node> st;
vector<ll> a;
int n,q;

void buildTree(int v,int tl, int tr){
	if (tl==tr){
		st[v].s=st[v].pre=st[v].suf=st[v].re=a[tl];
		return;
	}
	ll mid = (tl+tr)>>1LL;
	buildTree(v*2,tl,mid);
	buildTree(v*2+1,mid+1,tr);
	st[v].s=st[v*2].s+st[v*2+1].s;
	st[v].pre=max(st[v*2].pre,st[v*2].s+st[v*2+1].pre);
	st[v].suf=max(st[v*2+1].suf,st[v*2+1].s+st[v*2].suf);
	st[v].re=max(max(st[v*2].re,st[v*2+1].re),st[v*2].suf+st[v*2+1].pre);
}

void update(ll v,ll tl, ll tr,ll pos,ll val){
	if (tl==tr){
		st[v].s=st[v].pre=st[v].suf=st[v].re=val;
	}
	else {
		ll mid =(tl+tr)>>1LL;
		if (pos<=mid) update(v*2,tl,mid,pos,val);
		else update(v*2+1,mid+1,tr,pos,val);
		st[v].s=st[v*2].s+st[v*2+1].s;
		st[v].pre=max(st[v*2].pre,st[v*2].s+st[v*2+1].pre);
		st[v].suf=max(st[v*2+1].suf,st[v*2+1].s+st[v*2].suf);
		st[v].re=max(max(st[v*2].re,st[v*2+1].re),st[v*2].suf+st[v*2+1].pre);	
	}
}

 vector<long long> maximumSegmentSum(vector<int>& nums, vector<int>& q) {



	vector<ll> ans;

	int n=nums.size();
     a=vector<ll> (n,0);
     for(ll j=0;j<n;j++)
     {
         a[j]=nums[j];
     }
	st=vector<Node>(4*n,Node({0,0,0,0}));
	buildTree(1,0,n-1);
	for (int i=0;i<q.size();++i){
		ll x;
        x=q[i];
		update(1,0,n-1,x,-9e13);
		ans.push_back(max(st[1].re,0LL));
	}
     return ans;
 }

};```

