class Solution {
    public long[] maximumSegmentSum(int[] nums, int[] removeQueries) {
        int l = nums.length;
        long[] res = new long[l];

        UnionFind uf = new UnionFind(l, nums);

        long max = 0;

        int[] queries = new int[l];

        for(int i = removeQueries.length-1; i >= 0; i--){
            res[i] = max;

            int val = removeQueries[i];

            queries[val] = nums[val];

            if((val+1 < l && queries[val+1] == 0) && (val-1 >= 0 && queries[val-1] == 0)){ }
            else if((val+1 < l && queries[val+1] != 0) && (val-1 >= 0 && queries[val-1] != 0)){
                uf.union(val, val+1);
                uf.union(val, val-1);
            }
            else if(val+1 < l && queries[val+1] != 0){
                uf.union(val, val+1);
            }
            else if(val-1 >= 0 && queries[val-1] != 0){
                uf.union(val, val-1);
            }

            max = Math.max(max, uf.sum(val));
        }

        // res[0] = max;

        return res;
    }
}


class UnionFind {
    int[] nums;
    int[] parent;
    int[] rank;
    long[] sum;
    int count;

    public UnionFind(int n, int[] nums) { // for problem 200
        this.nums = nums;
        parent = new int[n];
        rank = new int[n];
        sum = new long[n];
        count = n;
        for (int i = 0; i < n; ++i) {
            parent[i] = i;
            rank[i] = 0;
            sum[i] = nums[i];
        }
    }

    public long sum(int x){
        return sum[find(x)];
    }

    public int find(int x) { // path compression
        // only the rank of the root matters, used in union op.
        if (parent[x] != x)
            parent[x] = find(parent[x]);
        return parent[x];
    }

    public void union(int x, int y) { // union with rank
        int rootx = find(x);
        int rooty = find(y);

        if (rootx != rooty) {
            parent[rootx] = rooty;
            sum[rooty] += sum[rootx];
            count--;
        }
    }

    public int getCount() {
        return count;
    }
}
