solutions.content:
# Intuition
1. traverse from back to front of removeQueries and use "Union find" to add each element to the group
2. keep track the "max segmention sum" when doing Union

# Code
```
class UnionFind{
public:
    int *parent;
    int *size;
    int group_size;
    long long *sum;
    long long MaxSum;
    UnionFind(int n)
    {
        parent = new int[n];
        size = new int[n];
        sum = new long long[n];
        group_size = n;
        MaxSum = 0;
        for(int i=0; i<n; i++)
        {
            parent[i] = i;
            size[i] = 1;
            sum[i] = 0;
        }
    }
    void add (int idx, int val)
    {
        sum[idx] = val;
        if(sum[idx] > MaxSum) MaxSum = sum[idx];
    }
    int find(int p)
    {
        int src = p;
        while(parent[src]!=src)
        {
            parent[src] = parent[parent[src]]; //compress tree while doing find
            src=parent[src];
        }
        return src;
    }
    bool Union(int p, int q)
    {
        int rootP = find(p);
        int rootQ = find(q);
        if(rootP == rootQ) return false;
        else
        {
            if(size[rootP] > size[rootQ])
            {
                parent[rootQ] = rootP;
                size[rootP]+= size[rootQ];
                sum[rootP] += sum[rootQ];
                if(sum[rootP] > MaxSum) MaxSum = sum[rootP];

            }
            else
            {
                parent[rootP] = rootQ;
                size[rootQ]+= size[rootP];
                sum[rootQ] += sum[rootP];
                if(sum[rootQ] > MaxSum) MaxSum = sum[rootQ];
            }
        }
        group_size--;
        return true;
    }
    void Print(int n)
    {
        cout<<"parent: ";
        for(int i=0; i<n; i++)
        {
            cout<<parent[i]<<" ";
        }
        cout<<"\n";

        cout<<"size: ";
        for(int i=0; i<n; i++)
        {
            cout<<size[i]<<" ";
        }
        cout<<"\n";        
    }
};
class Solution {
public:
    vector<long long> maximumSegmentSum(vector<int>& nums, vector<int>& removeQueries) {
        int n = removeQueries.size();
        vector<long long> vRet(n);
        vector<int> vTable(n, 0);
        UnionFind *uf = new UnionFind(n);
        for(int i=n-1; i>=1; i--)
        {
            int iRemoveIdx = removeQueries[i];
            vTable[iRemoveIdx] = nums[iRemoveIdx];
            uf->add(iRemoveIdx, nums[iRemoveIdx]);
            if(iRemoveIdx-1 >=0 && vTable[iRemoveIdx-1] >0) uf->Union(iRemoveIdx-1, iRemoveIdx);
            if(iRemoveIdx+1 <n && vTable[iRemoveIdx+1] >0) uf->Union(iRemoveIdx+1, iRemoveIdx);
            vRet[i-1] = uf->MaxSum;

        }

