class DSU {
public:
    vector<int> parent;
    vector<long long> sum;
    DSU (int size): parent(size), sum(size) {
        for (int i = 0; i < size; i+=1)
            parent[i] = i;
    }
    int find(int p) {
        if (this -> parent[p] != p)
            this -> parent[p] = this -> find(this -> parent[p]);
        return this -> parent[p];
    }
    void join(int p, int q) {
        int pp = this -> find(p), pq = this -> find(q);
        if (pp == pq) return;
        this -> parent[pq] = pp;
        this -> sum[pp] += this -> sum[pq];
    }
};

class Solution {
public:
    vector<long long> maximumSegmentSum(vector<int>& nums, vector<int>& removeQueries) {
        int size = nums.size();
