            if (idx + 1 < size && seen[idx + 1])
                dsu.join(idx, idx+1);
            if (idx - 1 >= 0 && seen[idx - 1])
                dsu.join(idx, idx-1);

            long long& cur = dsu.sum[dsu.find(idx)];
            ans[i-1] = cur > ans[i] ? cur: ans[i];
        }
        return ans;
    }
};
