function DJSet(n) {
    let p = Array(n).fill(-1), s = Array(n).fill(0); // s: group prefix sum
    return { find, union, update, sum, par }
    function find(x) {
        return p[x] < 0 ? x : p[x] = find(p[x]);
    }
    function union(x, y) {
        x = find(x);
        y = find(y);
        if (x == y) return false;
        if (p[x] < p[y]) [x, y] = [y, x];
        p[x] += p[y];
        p[y] = x;
        s[x] += s[y];
        return true;
    }
    function update(idx, v) {
        s[idx] = v;
        // s[idx] += v;
    }
    function sum() {
        return s;
    }
    function par() {
        return p;
    }
}
