const maximumSegmentSum = (a, b) => {
    let n = a.length, res = [0], ds = new DJSet(n), used = new Set(), max = 0;
    for (let i = n - 1; i >= 1; i--) {
        used.add(b[i]);
        ds.update(b[i], a[b[i]]);
        if (used.has(b[i] - 1)) ds.union(b[i], b[i] - 1);
        if (used.has(b[i] + 1)) ds.union(b[i], b[i] + 1);
        max = Math.max(max, ds.sum()[ds.find([b[i]])]);
        res.push(max);
    }
    return res.reverse();
};
