    void unionByRank(long long a,long long b){
        long long upa=findUPar(a);
        long long upb=findUPar(b);
        if(upa==upb){
            return;
        }
        if(rank[upa]>rank[upb])
        {
            par[upb] = upa;
            sum[upa] += sum[upb];
            mx = max(mx, sum[upa]);
        }
        else if(rank[upb]>rank[upa])
        {
            par[upa] = upb;
            sum[upb] += sum[upa];
            mx = max(mx, sum[upb]);
        }
        else{
            par[upa] = upb;
            rank[upb] ++;
            sum[upb] += sum[upa];
            mx = max(mx, sum[upb]);
        }
    }
    long long findUPar(long long a){
        if(par[a]==a){
            return a;
        }
        return par[a]=findUPar(par[a]);
    }
};
class Solution {
public:
    vector<long long> maximumSegmentSum(vector<int>& nums, vector<int>& q) {
        int n=nums.size();
        DisjointSet ds(n);
        ds.mx=0;
        vector<long long> ans(n,0);
        vector<int> vis(n,0);

        for(int i=n-1; i>0; i--){
            int ind=q[i];
            ds.sum[ind]=nums[ind];
            vis[ind]=1;
            ds.mx=max(ds.mx,(long long)nums[ind]);
            if(ind-1>=0 && vis[ind-1]){
                ds.unionByRank(ind,ind-1);
            }
            if(ind+1<n && vis[ind+1]){
                ds.unionByRank(ind,ind+1);
            }
            ans[i-1]=ds.mx;
        }
        return ans;
    }
};
