class Solution {
public:
vector<long long> maximumSegmentSum(vector<int>& nums, vector<int>& removeQueries) {
  int n = nums.size();
  vector<long long>arr(n + 2), pref(n + 2);
  multiset<long long>idx, vals;
  arr[0] = 0;
  pref[0] = 0;
  for (int i = 1; i <= n; i++) arr[i] = nums[i - 1];
  arr[n + 1] = 0;
  for (int i = 1; i <= n + 1; i++) pref[i] = pref[i - 1] + arr[i];
  idx.insert(0);
  idx.insert(n + 1);
  vals.insert(pref[n + 1]);
  vector<long long>ans;
  for (auto q : removeQueries) {
    auto it = idx.lower_bound(q + 1);
    int y = *it;
    it--;
    int x = *it;
    if (vals.find(pref[y - 1] - pref[x]) != vals.end()) vals.erase(vals.find(pref[y - 1] - pref[x]));
    if (q <= x) vals.insert(0);
    else vals.insert(pref[q] - pref[x]);
    if (y - 1 <= q + 1) vals.insert(0);
    else vals.insert(pref[y - 1] - pref[q + 1]);
    ans.push_back(*(--vals.end()));
    idx.insert(q + 1);
  }
  return ans;
}



};
