class Solution {
public:
    vector<long long> maximumSegmentSum(vector<int>& nums, vector<int>& rq) {

        int n=nums.size();
        vector<long long> ans(n,0);
        set<long long> s1;
        multiset<long long> s2;
        vector<long long> pre(n+1,0);
        pre[1]=nums[0];
        for(int i=1;i<n;i++) pre[i+1]=pre[i] +nums[i];
        // s1 contains all removed indexes
        // s2 contains all sum of segments

        <!--  // we wanna continuosly evaluate our problem b/w removed indexes so we initially assumed -1 ans n to be our removed indexes -->
        s1.insert(-1);
        s1.insert(n);
        s2.insert(pre[n]);
        for(int i=0;i<n;i++){
            auto it1=s1.upper_bound(rq[i]);
            int a=*it1;
            if(it1!=s1.begin()) {
                auto it2=--it1;
                int b=*it2;
                // deleting the sum of the broken segment and replacing it with sum of new segments
                auto q=s2.find(pre[a]-pre[b+1]);
                s2.erase(q);
                s2.insert(pre[a]-pre[rq[i]+1]);
                s2.insert(pre[rq[i]]-pre[b+1]);
                s1.insert(rq[i]);
            }
            ans[i]=*s2.rbegin();
        }
        return ans;
    }
};
