    {
        long a = find(x), b = find(y);
        if(rank[a]>rank[b])
        {
            parent[b] = a;
            sum[a] += sum[b];
            mx = max(mx, sum[a]);
        }
        else if(rank[b]>rank[a])
        {
            parent[a] = b;
            sum[b] += sum[a];
            mx = max(mx, sum[b]);
        }
        else{
            parent[a] = b;
            rank[b] ++;
            sum[b] += sum[a];
            mx = max(mx, sum[b]);
        }
