        {
            presum[i+1]=presum[i]+nums[i];
        }
        vector<long long> res(n);
        segement s(presum);
        for (int i=0; i<n; i++)
        {
            int k=removeQueries[i];
            s.remove(presum,k);
            res[i]=s.get();
        }
        return res;
    }
};

