class disjoint_set{
    int n;
    public:
    long long maxi=0;
    int* par;
    long long* sum;
    disjoint_set(int N){
        n=N;
        par=new int[n];
        sum=new long long[n];
        for(int i=0;i<n;i++){
            par[i]=-1;
            sum[i]=0;
        }
    }
    int find(int i){
        if(par[i]<0){
            return i;
        }
        int res=find(par[i]);
        par[i]=res;
        return res;
    }
    bool Union(int i,int j){
        int i_rep=find(i);
        int j_rep=find(j);
        par[i_rep]+=par[j_rep];
        sum[i_rep]+=sum[j_rep];
        par[j_rep]=i_rep;
        return 1;
    }
};

class Solution {
public:
    vector<long long> maximumSegmentSum(vector<int>& nums, vector<int>& q) {
        int n=nums.size();
        vector<long long> ans(n);
        disjoint_set* obj=new disjoint_set(n); 
        for(int i=n-1;i>0;i--){
            ans[i]=obj->maxi;
            int sum=nums[q[i]];
            if(q[i]>0){
                int x=obj->find(q[i]-1);
                if(obj->sum[x]!=0){
                    obj->Union(q[i]-1,q[i]);
                }
            }
            if(q[i]<n-1){
                int x=obj->find(q[i]+1);
                if(obj->sum[x]!=0){
                    obj->Union(q[i]+1,q[i]);
                }
            }
            int i_rep=obj->find(q[i]);
            obj->sum[i_rep]+=sum;
            obj->maxi=max(obj->maxi,obj->sum[i_rep]);
        }
        ans[0]=obj->maxi;
        return ans;
    }
};
