                if(obj->sum[x]!=0){
                    obj->Union(q[i]+1,q[i]);
                }
            }
            int i_rep=obj->find(q[i]);
            obj->sum[i_rep]+=sum;
            obj->maxi=max(obj->maxi,obj->sum[i_rep]);
        }
        ans[0]=obj->maxi;
        return ans;
