```go
func maximumSegmentSum(nums []int, removeQueries []int) []int64 {
  // We process in reverse and union segments together as we add elements.
  // We keep track of the maximum segment Sum and every time we add an element
  // (since we process in reverse, we add instead of removing) we try to add it 
  // to a new segment or create an existing segment, or merge two existing segments.
  // We update the max segment sum as we do that.
  n := len(nums)
  root := make([]int, n)
  rank := make([]int, n)
  for i := 0; i < len(root); i++ {
    root[i] = i
  }
  groupSum := make([]int64, n)
  grouped := make([]bool, n)
  res := make([]int64, n)
  var maxSeen int64
  for i := n-1; i >= 0; i-- {
    res[i] = maxSeen // the value at n-1 is always 0
    q := removeQueries[i]
    var leftSum, rightSum int64
    if q > 0 && grouped[q-1] {
      leftSum = groupSum[find(root, q-1)]
      union(root, rank, q, q-1)
    }
    if q < n-1 && grouped[q+1] {
      rightSum = groupSum[find(root, q+1)]
      union(root, rank, q, q+1)
    }
    sum := leftSum + rightSum + int64(nums[q])
    maxSeen = max(maxSeen, sum)
    groupSum[find(root, q)] = sum
    grouped[q] = true
  }
  return res
}

func find(root []int, x int) int {
  if root[x] == x {
    return x
  }
  root[x] = find(root, root[x])
  return root[x]
}

func union(root, rank []int, x, y int) {
  rootX := find(root, x)
  rootY := find(root, y)
  if rootX != rootY {
    if rank[rootX] > rank[rootY] {
      root[rootY] = rootX
    } else if rank[rootX] < rank[rootY] {
      root[rootX] = rootY
    } else {
      root[rootY] = rootX
      rank[rootX]++
    }
  }
}

func max(a, b int64) int64 {
  if a > b {
    return a
  }
  return b
}
