func maximumSegmentSum(nums []int, rq []int) []int64 {
	n := len(nums)
	uf := new(UF)
	uf.r = make([]int, n)
	uf.p = make([]int, n)
	for i := 0; i < n; i++ { uf.p[i] = i }
	res := make([]int64, len(rq))
	for i := len(rq) - 1; i >= 0; i-- {
		res[i] = int64(uf.mx)
        uf.SetR(rq[i], nums[rq[i]])
		for ni := rq[i] + 1; ni < n; ni++ {
			if uf.r[ni] != 0 && uf.Find(ni) != uf.Find(ni-1) {
				uf.Union(ni, ni-1)
			} else {
				break
			}
		}

		for ni := rq[i] - 1; ni >= 0; ni-- {
			if uf.r[ni] != 0 && uf.Find(ni) != uf.Find(ni+1) {
				uf.Union(ni, ni+1)
			} else {
				break
			}
		}
	}

	return res
}

type UF struct {
	p, r []int
	mx   int
}

func (uf *UF) SetR(e, v int) {
    uf.r[e] = v
    if uf.r[e] > uf.mx { uf.mx = uf.r[e] }
}
func (uf *UF) Find(e int) int {
	if uf.p[e] == e {
		return e
	}
	uf.p[e] = uf.Find(uf.p[e])
	return uf.p[e]
}

func (uf *UF) Union(e1, e2 int) {
	r1 := uf.Find(e1)
	r2 := uf.Find(e2)
	if r1 == r2 {
		return
	}

	switch {
	case uf.r[r1] < uf.r[r2]:
		uf.p[r1] = r2
		uf.r[r2] += uf.r[r1]
		if uf.r[r2] > uf.mx { uf.mx = uf.r[r2] }
	case uf.r[r1] >= uf.r[r2]:
		uf.p[r2] = r1
		uf.r[r1] += uf.r[r2]
		if uf.r[r1] > uf.mx { uf.mx = uf.r[r1] }
	}
	return
}
