class Solution {
    public long[] maximumSegmentSum(int[] nums, int[] q) {
        int n = nums.length;
        long[] ans = new long[n];

        UnionFind ob = new UnionFind(n);
        long max = 0;

        for(int i=n-1; i>0; i--) {
            int index = q[i];
            ob.sum[index] = nums[index];

            if(index+1<n && ob.sum[index+1] != 0) {
                ob.union(index, index+1);
            }
            if(index-1>-1 && ob.sum[index-1] != 0) {
                ob.union(index, index-1);
            }

            max = Math.max(max, ob.sum[index]);
            ans[i-1] = max;
        }
        return ans;
    }
}
class UnionFind {
    int[] par;
    long[] sum;
    UnionFind(int n) {
        par = new int[n];
        sum = new long[n];

        for(int i=0; i<n; i++) {
            par[i] = i;
        }
    }
    int findPar(int x) {
        if(this.par[x] == x) return x;
        return this.par[x] = findPar(this.par[x]);
    }
    void union(int x, int y) {
        int a = findPar(x);
        int b = findPar(y);

        par[b] = a;
        sum[a]+=sum[b];
    }
