class Solution {
public:


    int get(int i, vector<int>&parent){

        return parent[i] == i ? i :  parent[i] = get(parent[i], parent);
    }


    void Union(int n1, int n2, vector<int> &parent, vector<int> &rank, vector<long long> &sum){

        int p1 = get(n1, parent);
        int p2 = get(n2, parent);

        if(rank[p1] == rank[p2]){
            sum[p1] += sum[p2];
            parent[p2] = p1;
            rank[p1]++;
        }
        else if(rank[p1]<rank[p2]) parent[p1] = p2, sum[p2] += sum[p1];

        else parent[p2] = p1, sum[p1] += sum[p2];
    }


    vector<long long> maximumSegmentSum(vector<int>& nums, vector<int>& rq) {

        long long n = nums.size(), maxi = 0; // Maxi stores current maximum segement sum
        vector<long long> ans(n);
        vector<long long> sum(n,0); // sum[i] = sum of segment whose leader is  = i
        vector<int> parent(n, INT_MAX);
        vector<int> rank(n,0);
        ans[n-1]=0;

        for(int i = n-2;i>=0; i--){

            int node = rq[i+1];

            parent[node] = node;
            sum[node] = nums[node];

            if(node-1 >= 0 and parent[node - 1] != INT_MAX)
                Union(node, node-1, parent, rank, sum);

            if(node+1 < n and parent[node + 1] != INT_MAX)
                Union(node, node+1, parent, rank, sum);

            maxi = max(maxi, sum[get(node, parent)]);

            ans[i] = maxi;
        }        

        return ans;   
    }
};
