/**
 * @param {number[]} nums
 * @param {number[]} removeQueries
 * @return {number[]}
 */
var maximumSegmentSum = function(nums, removeQueries) {
    let res = [];
    // prefix sum
    let prefix = [];
    let s = 0;
    for(let i=0; i<nums.length; i++){
        s += nums[i];
        prefix.push(s);
    }

    res.push(0);
    let len = nums.length;
    // save interval, key means left side, value means right side
    let map1 = new Map();
    // save interval, key means right side, value means left side
    let map2 = new Map();

    for(let i=len-1; i>0; i--){
        let index = removeQueries[i];
        let sum = 0;
		// check if interval exist
		// interval not exist, save directly
        if(!map1.has(index+1) && !map2.has(index-1)){
            map1.set(index, index);
            map2.set(index, index);
            sum = index-1 >= 0 ? prefix[index] - prefix[index-1] : prefix[index];
		// interval exist, try to merge
        }else if(!map2.has(index-1)){
			// update right side
            let end = map1.get(index+1);
            map1.set(index, end);
			map2.set(end, index);
			// delete old data
            map1.delete(index+1);
            map2.delete(end);
            sum = index-1 >= 0 ? prefix[end] - prefix[index-1] : prefix[end];
		// interval exist, try to merge	
        }else if(!map1.has(index+1)){
			// update left side
            let start = map2.get(index-1);
            map2.set(index, start);
			map1.set(start, index);
			// delete old data
            map2.delete(index-1);
            map1.delete(start);
            sum = start-1 >= 0 ? prefix[index] - prefix[start-1] : prefix[index];
        }else{
            let end = map1.get(index+1);
            let start = map2.get(index-1);
            map1.delete(index+1);
            map2.delete(index-1);
            map1.set(start,end);
            map2.set(end, start);

            sum = start-1 >= 0 ? prefix[end] - prefix[start-1] : prefix[end];
        }
		// compare and get the max one
        if(sum > res[res.length-1]){
            res.push(sum);
        }else{
            let last = res[res.length-1];
            res.push(last);
        }
    }

    res = res.reverse();

    return res;    
};
