```
// TLE
#include <bits/stdc++.h>
using namespace std;
typedef long long ll;
class Solution {
public:
    vector<ll> maximumSegmentSum(vector<int>& nums, vector<int>& removeQueries) {
        vector<ll> res;
		priority_queue<pair<ll, pair<int, int>>> pq;
		set<int> removed;
		ll sum = 0;
		for (int i = 0; i < nums.size(); i++) {
			sum += nums[i];
		}
		vector<ll> prefix(nums.size() + 1, 0);
		for (int i = 0; i < nums.size(); i++) {
			prefix[i + 1] = prefix[i] + (ll)nums[i];
		}
		pq.emplace(sum, make_pair(0, nums.size() - 1));
		for (int i = 0; i < removeQueries.size() - 1; i++) {
			removed.insert(removeQueries[i]);
			bool done = false;
			while (!done) {
				auto curr = pq.top(); 
				ll sum = curr.first;
				int l = curr.second.first, r = curr.second.second;
				// if (l > r) continue;
				auto itr = lower_bound(removed.begin(), removed.end(), l);
				if (itr == removed.end() || *itr > r) {
					done = true;
					res.push_back(sum);
				} else {
					pq.pop();
					if (*itr == l && l < r) {
						pq.emplace(prefix[r + 1] - prefix[l + 1], make_pair(l + 1, r));
					} else if (*itr == r && l < r) {
						pq.emplace(prefix[r] - prefix[l], make_pair(l, r - 1));
					} else if (*itr > l && *itr < r) {
						pq.emplace(prefix[(*itr)] - prefix[l], make_pair(l, (*itr) - 1));
						pq.emplace(prefix[r + 1] - prefix[(*itr) + 1], make_pair((*itr) + 1, r));
					}
				}
			}
		}
		res.push_back(0);
		return res;
    }
};
