Need to notice that when removing an element from the multiset, we need to remove the iterator instead of by value, the latter one will remove all element has that value. 
```
// Accepted
#include <bits/stdc++.h>
using namespace std;
typedef long long ll;
class Solution {
public:
    vector<ll> maximumSegmentSum(vector<int>& nums, vector<int>& removeQueries) {
		int n = nums.size();
        multiset<ll> valid;
		set<int> removed;
		vector<ll> S(n + 1, 0);
		vector<ll> res;
		for (int i = 0; i < n; i++) S[i + 1] = S[i] + nums[i];
		valid.insert(S[n]);
		valid.insert(0);
		removed.insert(-1);
		removed.insert(n);
		for (int i = 0; i < n; i++) {
			removed.insert(removeQueries[i]);
			auto pre = prev(removed.find(removeQueries[i]));
			auto ne = next(removed.find(removeQueries[i]));
			int l = *pre, r = *ne;
            valid.erase(valid.find(S[r] - S[l + 1]));
			if (l + 1 != removeQueries[i]) {
				valid.insert(S[removeQueries[i]] - S[l + 1]);
			}
			if (r - 1 != removeQueries[i]) {
				valid.insert(S[r] - S[removeQueries[i] + 1]);
			}
			res.push_back(*prev(valid.end()));
		}
		return res;
    }
};
