It is kind of complicated.

The time complexity is O(N(logN)^2).
The merge function costs (logN)^2 beacuse there is a segment tree used to maintain the interval sum in it and it executes logN times.
Should I call it 2D-segment tree?

I feel incredible when it works.
Just want ro record something.
If you can understand it , that is great.
```
class Sgtree2{
    public:
    vector<long long> v;
    Sgtree2(vector<int>& input){
        int n = input.size();
        v = vector<long long>(4*n,0);
        build(0,n-1,0,input);
    }
    void build(int l , int r, int node,vector<int>& arr){
        if(l==r){
            v[node] = arr[l];
            return ;
        }
        int m = (l+r)/2;
        build(l,m,node*2+1,arr);
        build(m+1,r,node*2+2,arr);
        v[node] = v[node*2+1]+v[node*2+2];
    }
    void update(int l , int r,int node , int pos,int val){
        if(l==r){
            v[node] = val;
            return;
        }
        int m = (l+r)/2;
        if(pos>=l && pos <= m ){
            update(l,m,node*2+1,pos,val);
        }else{
            update(m+1,r,node*2+2,pos,val);
        }
        v[node] = v[node*2+1]+v[node*2+2];
    }
    long long query(int l , int r, int L , int R, int node){
        if(l==L && r == R){
            return v[node];
        }
        int M = (L+R)/2;
        if( r <= M ){
            return query(l,r,L,M,node*2+1);
        }else if(l>M){
            return query(l,r,M+1,R,node*2+2);
        }else{
            return query(l,M,L,M,node*2+1) + query(M+1,r,M+1,R,node*2+2);
        }
    }

};
class Sgtree{
    public:
    vector<long long> v;
    vector<int> left,right;//the leftmost/rightmost value of interval of v[node] 
    vector<int> leftzero , rightzero;//leftmost/rightmost zero's idx of interval of v[node].

    int n ;
    Sgtree2 *pre;
    Sgtree(vector<int>& arr,Sgtree2 *t){
        n = arr.size();
        v = vector<long long>(4*n,0);
        left = vector<int>(4*n,0);
        right = vector<int>(4*n,0);

        leftzero = vector<int>(4*n,-1);
        rightzero = vector<int>(4*n,-1);

        pre = t;
        build(0,n-1,0,arr);
    }
    void build(int l , int r, int node,vector<int>& arr){
        if(l==r){
            v[node] = arr[l];
            left[node]=arr[l];
            right[node]=arr[l];
            if(arr[l]==0){
                leftzero[node] = l;
                rightzero[node] = l;
            }
            return ;
        }
        int m = (l+r)/2;
        build(l,m,node*2+1,arr);
        build(m+1,r,node*2+2,arr);
        merge(l,r,node);
    }
    long long update(int l , int r , int node , int pos , int val){
        if(l==r){
            v[node] = val;
            left[node]=val;
            right[node]=val;
            if(val==0){
                leftzero[node] = l;
                rightzero[node] = l;
            }
            pre->update(0,n-1,0,pos,val);
            return v[node];
        }
        int m = (l+r)/2;
        if(pos>=l && pos <= m ){
            update(l,m,node*2+1,pos,val);
        }else{
            update(m+1,r,node*2+2,pos,val);
        }

        merge(l,r,node);
        return v[node];
    }
    void merge(int l ,int r , int node){
        if(right[node*2+1] == 0 || left[node*2+2] == 0 ){
            v[node] = max(v[node*2+1],v[node*2+2]);
        }else{
            if(rightzero[node*2+1] == -1 && leftzero[node*2+2]==-1){
                v[node] = v[node*2+1]+v[node*2+2];
            }else if(rightzero[node*2+1] == -1){
                int idx = leftzero[node*2+2];
                v[node] = max( pre->query( l , idx ,0 ,n-1,0) , v[node*2+2] );
            }else if(leftzero[node*2+2]==-1){
                int idx = rightzero[node*2+1];
                v[node] = max( pre->query( idx , r ,0 ,n-1,0) , v[node*2+1] );
            }else{
                int tl = rightzero[node*2+1];
                int tr = leftzero[node*2+2];
                v[node] = max( {v[node*2+1],v[node*2+2] , pre->query( tl , tr ,0 ,n-1,0) } );
            }
        }
        left[node] = left[node*2+1];
        right[node] = right[node*2+2];
        if(leftzero[node*2+1]==-1){
            leftzero[node] = leftzero[node*2+2];
        }else{
            leftzero[node] = leftzero[node*2+1];
        }
        if(rightzero[node*2+2]==-1){
            rightzero[node] = rightzero[node*2+1];
        }else{
            rightzero[node] = rightzero[node*2+2];
        }
    }

};
class Solution {
public:
    vector<long long> maximumSegmentSum(vector<int>& nums, vector<int>& removeQueries) {

        //time O(n(logn)^2 );
        int n = nums.size();
        vector<long long> ans;
        Sgtree2 * pre = new Sgtree2(nums);
        Sgtree *sgt = new Sgtree(nums,pre);

        for(auto&& i : removeQueries){
            ans.push_back(sgt->update(0,n-1,0,i,0));
        }

        return ans;
    }
};
