class Solution {

public:
    #define LL long long


    multiset<LL> segsum;
    LL prefix[100010] = {0};
    vector<LL> ans;
    set<int> qid;
    int n;

    void split(int l, int r, int at){
        LL ori = prefix[r-1] - prefix[l];
        LL n1 = prefix[at-1] - prefix[l];
        LL n2 = prefix[r-1] - prefix[at];

        segsum.erase(segsum.lower_bound(ori)); // erase only 1 element, not all element with same value
        segsum.insert(n1);
        segsum.insert(n2);

        ans.push_back(*prev(segsum.end()));
    }

    pair<int,int> findSegRange(int at){
        auto pos = qid.lower_bound(at);
        int lo = *prev(pos) ;
        int hi = *pos;

        return {lo, hi};
    }

    vector<long long> maximumSegmentSum(vector<int>& nums, vector<int>& removeQueries) {
        n = nums.size();

        for(int i=1; i<=n; i++) prefix[i] = prefix[i-1] + nums[i-1];
        qid.insert(0);
        qid.insert(n+1);
        segsum.insert(prefix[n]);

        for(int q : removeQueries){
            auto range = findSegRange(q+1);
            split(range.first, range.second, q+1);
            qid.insert(q+1);
        }

        return ans;
    }
};
