class Solution {
public:
    vector<long long> maximumSegmentSum(vector<int>& nums, vector<int>& removeQueries) {
        map<long long,long long>mp;
        int n=nums.size();
        set<int>remove{-1,n};
        multiset<long long>temp;
        vector<long long>ans(n,0);
        long long sum=0;
        for(int i=0;i<n;i++){
            sum+=nums[i];
            mp[i]=sum;
