            long long t1=mp[*(r)-1]-mp[removeQueries[i]];
            long long t2=mp[removeQueries[i]-1]-mp[*(l)];
            remove.insert(removeQueries[i]);
            temp.insert(t1);
            temp.insert(t2);
            ans[i]=*(temp.rbegin());
        }
