class Solution {
public:
    vector<long long> maximumSegmentSum(vector<int>& nums, vector<int>& removeQueries) {

        int n=nums.size();  
        long long int pref[n+1];
        pref[0]=0;

        for(int i=0;i<n;i++)
        {
            pref[i+1]=pref[i]+nums[i];
        }

        set<int>idx;
        multiset<long long int>s;
        s.insert(pref[n]);
        idx.insert(0);
        idx.insert(n+1);

        vector<long long>ans;

        for(auto it:removeQueries)
        {
            it++;
            auto itt=idx.upper_bound(it);
            int sec=*itt;
            itt--;
            int f=*itt;

            long long int val=pref[sec-1]-pref[f];

            auto xi=s.find(val);
            if(xi!=s.end())
            s.erase(xi);

            s.insert(pref[it-1]-pref[f]);
            s.insert(pref[sec-1]-pref[it]);
            idx.insert(it);

            ans.push_back(*s.rbegin());    
        }
        return ans;   
    }
};

